def threeSum(nums):
    d = {}
    tmp_d = {}
    for n in nums:
        if n not in tmp_d:
            tmp_d[n] = 1
        else:
            tmp_d[n] +=1
    for k,v in tmp_d.items():
        if v > 3:
            for _ in range(0,v-3):
                nums.remove(k)
    tmp_d = {}
    for i, n in enumerate(nums):
        if n not in tmp_d:
            tmp_d[n] = i

    for i1, a in enumerate(nums):
        for i2, b in enumerate(nums):
            neg_sum = (a + b) * -1
            if i1 != i2 and neg_sum in tmp_d and i2 != tmp_d[neg_sum] and i1 != tmp_d[neg_sum]:
                tmp = [a, b, neg_sum]
                tmp.sort()
                tmp = tuple(tmp)
                _hash = hash(tmp)
                if _hash not in d:
                    d[_hash] = list(tmp)
    return list(d.values())

