def reverse(x):
    if len(bin(abs(x))[2:]) > 32:
        return 0
    a = []
    is_neg = True if x < 0 else False
    if x < 10 and not is_neg:
        return x
    x = abs(x)
    while x > 0:
        remain = x % 10
        x = int(x / 10)
        a.append(remain)
    num = 0
    index = 0
    while a:
        num += a.pop() * (10 ** index)
        index += 1
    return num if not is_neg else num*-1

print(reverse(-21474834132))